mapper.select:
SELECT	EMP_NO, NAME, DEPT, JGRADE, PH_CON, USER_ID, USER_PWD, ADMIN_CK, SESSIONKEY, SESSIONLIMIT
FROM	EMPLOYEE_TBL
WHERE 	ID = #{id}
AND		PWD = #{pwd}

mapper.update:

	UPDATE 	EMPLOYEE_TBL
	SET		SESSIONKEY = #{sessionId},
			SESSIONLIMIT = #{next}
	WHERE	ID = #{id}

UPDATE	SPRING_USER_TBL
SET		SESSIONLIMIT = #{next}
WHERE	SESSIONKEY=#{sessionId}
